import React, { Component } from "react"
import './styles.css';

export class Emaildata extends Component {
    constructor(props) {
        super();
        this.state = {
            id: ''
        }
    }
    Handleemail = (email) => {
        this.setState({
            id: email
        })
    }

    Checkemail = (email) => {
        var email = document.getElementById("email").value;
	    var message = "Invalid email address"
	    var ucsb = /\S+@\S+\b.com\b/;
	    var com = /\S+@+\bucsb.edu\b/;

	    document
		    .getElementById("error")
		    .innerHTML = message;

		    if(ucsb.test(email) || com.test(email)) {
			    document.getElementById("error").innerHTML = "Email succesfully recorded";
		    }
    }

    render() {
        return (
            <div>
                <div className = "header">
                    <h1>Email</h1>
                </div>
                <div className = "text">
                    <div>
                        <form onSubmit = {this.Checkemail}>
                            <label for = "email">Email:</label><br/>
                            <input type = "text" id="email" name = "email" required onChange = {this.Handleemail}></input><br/><br/>
                            <input type = "submit" id = "submit" name = "submit" value = "Submit"></input>
                        </form>
                    </div>
                    <br/><div id="error"></div>
                </div>
            </div>
        );
    }
}

export default Emaildata
