import React, { Component } from "react"
import './styles.css';

export class Emaildata extends Component {
    constructor(props) {
        super();
        this.state = {
            id: ''
        }
    }

    Handleemail(email) {
        this.setState({id: email.target.value});
    }

    Checkemail = () => {
	    var message = "Invalid email address"
	    var ucsb = /\S+@\S+\b.com\b/;
	    var com = /\S+@+\bucsb.edu\b/;

	    document
		    .getElementById("error")
		    .innerHTML = message;

		    if(ucsb.test(this.state.id) || com.test(this.state.id)) {
			    document.getElementById("error").innerHTML = "Email succesfully recorded";
		    }
    }

    render() {
        return (
            <div>
                <div className = "header">
                    <h1>Email</h1>
                </div>
                <div className = "text">
                    <div>
                        <label for = "email">Email:</label><br/>
                        <input type = "text" id="email" name = "email" value = {this.state.email} required onChange = {this.Handleemail}></input><br/><br/>
                        <button type = "button" onClick = {this.Checkemail}>Submit</button>
                    </div>
                    <br/><br/><div id="error"></div>
                </div>
            </div>
        );
    }
}

export default Emaildata
