import React, { Component } from "react"
import './styles.css';

export class Emaildata extends Component {
    constructor(props) {
        super();
        this.state = {
            id: ''
        }
    }

    Checkemail = () => {
        var email = document.getElementById("email").value;
	    var message = "Invalid email address"
	    var ucsb = /\S+@\S+\b.com\b/;
	    var com = /\S+@+\bucsb.edu\b/;

	    document
		    .getElementById("error")
		    .innerHTML = message;

		    if(ucsb.test(email) || com.test(email)) {
			    document.getElementById("error").innerHTML = "Email succesfully recorded";
		    }
    }

    render() {
        return (
            <div>
                <div className = "header">
                    <h1>Email</h1>
                </div>
                <div className = "text">
                    <form onSubmit={this.Checkemail}>
                    <div>
                        <label for="email">Email:</label><br/>
                        <input type="text" id="email" name = "email" required></input><br/><br/>
                        <button onclick="verifyEmail();">Submit</button><br/><br/>
                    </div>
                    <br/><div id="error"></div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Emaildata
