import './App.css';
import Body from './component/body'
import Tablist from './component/tablist'
import { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      active: 4
    }
    this.changetab =(id) =>{
      this.setState({
        active: id
      })
    }
  }

  render(){
    const tabs =[{
      id: 1,
      title: 'Text'
    },
    {
      id: 2,
      title: 'Image'
    },
    {
      id: 3,
      title: 'Video'
    },
    {
      id: 4,
      title: 'Table'
    },
    {
      id: 5,
      title: 'Email'
    }]

    return (
      <div className="App">
        <h1>Tabs Demo</h1>
        <div className="navigation-bar">
          {}
          <Tablist tabs = {tabs} activetab = {this.state.active} ctab = {this.changetab}/>
        </div>
        <div className = "body">
          <Body activetab = {this.state.active}/>
        </div>
      </div>
    );
  }
}

export default App;
